import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Overlay } from './components/Overlay';
import { ThreeManager } from './services/threeManager';
import { GestureService } from './services/gestureService';
import { AppState, ParticleShape } from './types';
import { AUDIO_URL } from './constants';

const LOGO_URL = "data:image/png;base64,";

const App: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const threeManagerRef = useRef<ThreeManager | null>(null);
  const gestureServiceRef = useRef<GestureService | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const [appState, setAppState] = useState<AppState>(AppState.IDLE);
  const [currentShape, setCurrentShape] = useState<ParticleShape>(ParticleShape.TREE);
  const [error, setError] = useState<string>('');

  const handleShapeChange = useCallback((shape: ParticleShape) => {
    setCurrentShape(shape);
    if (threeManagerRef.current) {
      threeManagerRef.current.setShape(shape);
    }
  }, []);

  const startExperience = async () => {
    setAppState(AppState.LOADING);
    
    try {
      if (!audioRef.current) {
        audioRef.current = new Audio(AUDIO_URL);
        audioRef.current.loop = true;
        audioRef.current.volume = 0.5;
      }
      await audioRef.current.play();
    } catch (e) {
      console.warn("Audio autoplay blocked", e);
    }

    if (containerRef.current && !threeManagerRef.current) {
      threeManagerRef.current = new ThreeManager(containerRef.current);
    }

    try {
      if (videoRef.current && canvasRef.current) {
        gestureServiceRef.current = new GestureService(
          videoRef.current, 
          canvasRef.current,
          handleShapeChange
        );
        await gestureServiceRef.current.start();
        setAppState(AppState.RUNNING);
      } else {
        throw new Error("Video or Canvas element not found");
      }
    } catch (err) {
      console.error(err);
      setError("Camera access denied or MediaPipe error.");
      setAppState(AppState.ERROR);
    }
  };

  useEffect(() => {
    return () => {
      if (threeManagerRef.current) threeManagerRef.current.cleanup();
      if (gestureServiceRef.current) gestureServiceRef.current.stop();
      if (audioRef.current) {
          audioRef.current.pause();
          audioRef.current = null;
      }
    };
  }, []);

  return (
    <div className="relative w-full h-full bg-black overflow-hidden">
      <img 
        src={LOGO_URL} 
        alt="Kite Holiday Logo" 
        className="fixed top-6 right-6 w-24 md:w-32 h-auto drop-shadow-2xl pointer-events-none object-contain"
        style={{ zIndex: 9999 }}
      />

      <Overlay 
        appState={appState} 
        currentShape={currentShape} 
        onStart={startExperience}
        errorMsg={error}
      />
      
      <div ref={containerRef} id="canvas-container" className="w-full h-full" />
      
      <video 
        ref={videoRef}
        className="hidden"
        playsInline
        muted
      />

      <div className={`fixed bottom-4 left-4 z-50 flex flex-col gap-2 md:gap-3 transition-opacity duration-700 ${appState === AppState.RUNNING ? 'opacity-90' : 'opacity-0 pointer-events-none'}`}>
        <div className="flex flex-col gap-1 md:gap-1.5 font-mono text-[10px] md:text-sm">
          <div className={`px-2 md:px-3 py-1 md:py-1.5 rounded bg-black/60 backdrop-blur-md border-l-2 md:border-l-4 transition-all duration-300 ${currentShape === ParticleShape.EXPLODE ? "text-white font-bold border-yellow-500 scale-105 shadow-[0_0_15px_rgba(234,179,8,0.3)]" : "text-gray-500 border-transparent opacity-40"}`}>
            üñê OPEN: EXPLODE
          </div>
          <div className={`px-2 md:px-3 py-1 md:py-1.5 rounded bg-black/60 backdrop-blur-md border-l-2 md:border-l-4 transition-all duration-300 ${currentShape === ParticleShape.TREE ? "text-white font-bold border-yellow-500 scale-105 shadow-[0_0_15px_rgba(234,179,8,0.3)]" : "text-gray-500 border-transparent opacity-40"}`}>
            ‚úä FIST: TREE
          </div>
        </div>

        <canvas 
          ref={canvasRef}
          className="w-20 md:w-60 rounded-lg border border-yellow-500/30 bg-black shadow-2xl"
          style={{ transform: 'scaleX(-1)' }}
        />
      </div>
    </div>
  );
};

export default App;